import React from 'react';
import classNames from 'classnames';

export default ({ show, noOverlay = false }) => show && (
    <div
        className={classNames({
            'loader-container': noOverlay,
            'full-screen-container': !noOverlay,
        })}
    >
        <div className="loader" />
    </div>
);
