import React, { useState } from "react";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import { TextField } from "formik-material-ui";
import {
  Grid,
  Typography,
  Button,
  makeStyles,
  IconButton,
  InputAdornment,
} from "@material-ui/core";
import { useDispatch } from "react-redux";
import { ApplyLeave } from "store/action";
import { Visibility, VisibilityOff } from "@material-ui/icons";

const ApplyLeaveSchema = Yup.object().shape({
  leaveType: Yup.string().required("Required"),
  duration: Yup.string().required("Required"),
  fromDate: Yup.string().required("Required"),
  toDate: Yup.string().required("Required"),
  description: Yup.string().required("Required"),
  designation: Yup.string().required("Required"),
  empid: Yup.string().required("Required"),
  salary: Yup.string().required("Required"),
  accno: Yup.string().required("Required"),
  ifsc: Yup.string().required("Required"),
  doj: Yup.string().required("Required"),
  email: Yup.string().required("Required"),

});

const useStyles = makeStyles((theme) => ({
  submit: {
    margin: theme.spacing(3, 0, 2),
    width: theme.spacing(12),
    backgroundColor: "#1769aa",
  },
  buttons: {
    display: "flex",
    justifyContent: "flex-end",
  },
  button: {
    marginTop: theme.spacing(3),
    marginLeft: theme.spacing(1),
  },
  formControl: {
    minWidth: "100%",
    marginTop: "16px",
  },
}));

export default function Request({ handleNext, handleBack }) {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [open, setOpen] = React.useState(false);
  const [openOne, setOpenOne] = React.useState(false);

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };
  const handleCloseOne = () => {
    setOpenOne(false);
  };

  const handleOpenOne = () => {
    setOpenOne(true);
  };  


  const handleSubmit = async (value) => {
    console.log(value);
    const {
      designation,
      empid,
      salary,
      accno,
      ifsc,
      doj,
      email, leaveType, description, fromDate, toDate, duration
    } = value;
    const obj = {
      designation: designation,
      userId: empid,
      salary: salary,
      accountNo: accno,
      ifsc: ifsc,
      doj: doj,
      officialEmail: email,
      leaveType: leaveType,
      description: description,
      fromDate: fromDate,
      toDate: toDate,
      duration: duration,
    };
    dispatch(ApplyLeave(obj)).then((res) => {});
    handleNext();
  };

  return (
    <Formik
      initialValues={{
        designation: "",
        empid: "",
        salary: "",
        accno: "",
        ifsc: "",
        doj: "",
        email: "",
        leaveType: "",
        duration: "",
        fromDate: "",
        toDate: "",
        description: "",
      }}
      validationSchema={ApplyLeaveSchema}
      enableReinitialize={true}
      validateOnChange={true}
      onSubmit={async (values) => {
        handleSubmit(values);
      }}
    >
      {({ setFieldValue, values }) => (
        <div>
          {console.log("values", values)}
          <Typography variant="h6" gutterBottom>
            Job Information
          </Typography>
          <Form>
            <Grid container spacing={3}>
              <Grid item xs={12} md={4}>
                <Field
                  component={TextField}
                  variant="standard"
                  margin="normal"
                  fullWidth
                  id="designation"
                  label="Designation"
                  name="designation"
                  disable="false"
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <Field
                  component={TextField}
                  variant="standard"
                  margin="normal"
                  fullWidth
                  name="empid"
                  id="employee id"
                  label="Employee id"
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <Field
                  component={TextField}
                  variant="standard"
                  margin="normal"
                  fullWidth
                  name="salary"
                  id="salary"
                  label="Salary $"
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <Field
                  component={TextField}
                  variant="standard"
                  margin="normal"
                  fullWidth
                  id="date"
                  label="Date Of Joining"
                  name="doj"
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <Field
                  component={TextField}
                  variant="standard"
                  margin="normal"
                  fullWidth
                  name="accno"
                  id="acc no."
                  label="Account number"
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <Field
                  component={TextField}
                  variant="standard"
                  margin="normal"
                  fullWidth
                  name="ifsc"
                  id="IFSC"
                  label="IFS Code"
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <Field
                  component={TextField}
                  variant="standard"
                  margin="normal"
                  fullWidth
                  name="email"
                  id="E-mail"
                  label="Officail mail id"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                    <Field
                      component={TextField}
                      variant="standard"
                      margin="normal"
                      fullWidth
                      id="fromDate"
                      label="From Date"
                      name="fromDate"
                    />
                  </Grid>

                  <Grid item xs={12} sm={6}>
                    <Field
                      component={TextField}
                      variant="standard"
                      margin="normal"
                      fullWidth
                      id="toDate"
                      label="To Date"
                      name="toDate"
                    />
                  </Grid>

                  <Grid item xs={12}>
                    <Field
                      component={TextField}
                      variant="standard"
                      margin="normal"
                      fullWidth
                      id="description"
                      label="Reason for leave"
                      name="description"
                    />
                  </Grid>


            </Grid>
            <div className={classes.buttons}>
              <Button onClick={handleBack} className={classes.button}>
                Back
              </Button>

              <Button
                variant="contained"
                color="primary"
                type="submit"
                // onClick={handleNext}
                className={classes.button}
              >
                Save & Next
              </Button>
            </div>
          </Form>
        </div>
      )}
    </Formik>
  );
}
